import React from 'react'
import '../styles/home.css';
import {motion} from 'framer-motion';
import Typewriter from 'typewriter-effect';

export const Home = () => {
  const image= {
    hidden:{
      opacity: 0,
    },
    show:{
      opacity: 1,
      x: [-50, 0],
      transition:{
        delay:3,
        duration:2 
      }
    }
  }
  
  return (
    <div className='home' id='home'> 
      <div className='logo_home'>**logo**</div>
      <div className='divisor_portada'>
        <div className='parrafo_portada'>
          <div className='div_typewriter'>
            <Typewriter 
              onInit={(Typewriter) => {
                Typewriter
                .changeDelay(75)
                // .typeString("Hello everyone! I'm Leonardo Tolaba an " + '<strong>Industrial Mechanic</strong>')
                // .pauseFor(500)
                // .changeDeleteSpeed(10)
                // .deleteChars(22)
                // .typeString("an " + '<strong>Electrician</strong>')
                // .pauseFor(500)
                // .deleteChars(14)
                // // .typeString("a "+ '<strong>Maintenance Officer</strong>')
                // // .pauseFor(500)
                // // .deleteChars(21)
                // .typeString("an "+ '<strong>Administrative</strong>')
                // .pauseFor(500)
                // .deleteChars(17)
                // .typeString("a "+ '<strong>Frontend Developer</strong>')
                // .pauseFor(500)
                // .deleteChars(18)
                // .typeString('<strong>Backend Developer</strong>')
                // .pauseFor(500)
                // .deleteChars(20)
                .typeString('Hello everyone!')
                .typeString('<br></br>')
                .typeString("I'm Leonardo Tolaba a ")
                .typeString('<br>')
                .typeString('<strong><span style="color: #fadd46;">Fullstack Developer!</span></strong>')
                .start();
              }}>
          </Typewriter>
          </div>
          <div className='btns_home'>
            <button className='btn_home'>Contactame</button>
            <button className='btn_home'>Descargar CV</button>
          </div>

        
        </div>   
        <motion.div className='redes_portada' variants={image} initial="hidden" animate="show" >
            {/* <motion.img  className='foto_home' src={require('../image/newfoto.png')} alt="imagen de portada"></motion.img> */}
        </motion.div>
      </div>        
    </div>
  )
}
