import React, { useRef } from 'react';
import '../styles/biography.css';
import {motion} from 'framer-motion'





export const Biography = () => {
  const biography = useRef();
  const details= {
    hidden:{
      opacity: 0,
      
      x: -50
    },
    visible: {
      opacity: 1,
      x: 0,
      transition: {
        duration: 1,
      }
    }
  }
  return (
    <motion.div className='container_ppal_project' ref={biography}>
        <motion.h2 className='subtitulo' initial={{y: -150}} animate={{y:-10}} transition={{delay: 0.1, type: 'spring', stiffness: 75}}>A cerca de mí</motion.h2>
        {/* <motion.div className='biography_details' initial={{opacity: 0}} animate={{opacity: 1}} transition={{delay: 0.5, duration: 1}}> */}
        <motion.div className='biography_details' 
        // variants={details}
        // initial="hidden" 
        // animate="visible" 
        initial={"offscreen"}
        whileInView={"onscreen"}
        viewport={{once: false, amount: 1}}
        transition={{duration: 2}}
        >
             <p className='biography_description'>Hola gracias por visitar mi portafolio soy Leonardo Tolaba y es un gusto poder mostrarte lo que es mi mundo. Soy estudiante de la Tecnicatura en Programación en la UTN y a su vez me encuentro realizando el curso brindado por Oracle One para convertirme en desarrollador FrontEnd. Me gustaria poder tener mi primera experiencia como desarrollador y poder capacitarme cada día más. Soy una persona proactiva y me encanta aprender. </p>
            <div className='imagen_bio'>
                <img src={require('../image/lion.png')} className='imagen_foto' alt='imq'></img>
            </div>
        </motion.div>
    </motion.div>
  )
}
