import React, { useRef } from 'react';
import {Routes, Route,BrowserRouter, useHref} from "react-router-dom";
import { Biography } from '../components/Biography';
import { Contact } from '../components/Contact';
import { Error } from '../components/Error';
import { Header } from '../components/Header';
import { NavLink } from 'react-router-dom';
import { Home } from '../components/Home';
import { Knowledge } from '../components/Knowledge';
import { Projects } from '../components/Projects';


export const MisRutas = () => {
  const home = useRef();
  const bio = useRef();
  const knowledge = useRef();
  const project = useRef();
  const contact = useRef();

  const scrollToSection = (elementRef) => {
    window.scrollTo({
      top: elementRef.current.offsetTop,
      behavior: "smooth",
    })
  }
  return (
    <BrowserRouter>
        <div className='contenedor'>
        <div className='header'>
            <nav className='menu_header'>
            <ul className='listado_header'>
                <li className='listado'>
                  <a to="/Home" className="navLink"  onClick={()=> scrollToSection(home)}>X</a>
                </li>
                <li className='listado'>
                  <a to="/Biography" className="navLink" onClick={()=> scrollToSection(bio)}>X</a>
                </li>
                <li className='listado'>
                  <a to="/Knowledge" className="navLink" onClick={()=> scrollToSection(knowledge)}>X</a>
                </li>
                <li className='listado'>
                  <a to="/Projects" className="navLink" onClick={()=> scrollToSection(project)}>X</a>
                </li>
                <li className='listado'>
                  <a to="/Contact" className="navLink" onClick={()=> scrollToSection(contact)}>X</a>
                </li>
            </ul>
            </nav>
        </div>
          <motion.section className='container_ppal_project' ref={home}><Home/></motion.section>
          <motion.section className='container_ppal_project'  ref={bio}><Biography/></motion.section>
          <motion.section className='container_ppal_project' ref={knowledge}
                  initial={"offscreen"}
                  whileInView={"onscreen"}
                  viewport={{once:false, amount:0.5}}
                  transition={{staggerChildren:0.5}}><Knowledge/></motion.section>
          <motion.section className='container_ppal_project' ref={project}><Projects/></motion.section>
          <motion.section className='container_ppal_project' ref={contact}><Contact/> </motion.section>
        </div>   
          
    </BrowserRouter>
  )
}
        {/* <Header/> */}
        {/* <Routes>
            <Route path='/' element={<Home/>}></Route>
            <Route path='/Home' element={<Home/>}></Route>
            <Route path='/Biography' element={<Biography/>}></Route>
            <Route path='/Knowledge' element={<Knowledge/>}></Route>
            <Route path='/Projects' element={<Projects/>}></Route>
            <Route path='/Contact' element={<Contact/>}></Route>
            <Route path='*' element={<Error/>}></Route>
        </Routes> */}
