import React from 'react'
import '../styles/knowledge.css';
import {motion } from 'framer-motion';


export const KnowledgeModal = ({data, close}) => {
  return (

    <motion.div className='modal' 
    onClick={(e)=>{
        e.stopPropagation();
    }}
    >   
       <div className='knowledge_modal'>
        <h2 className='tittle_card_modal'>{data.tittle}</h2>
        <img className='icon_inside_modal' src={data.image}></img>
        

        </div> 
        <button className='modal_btn' onClick={close}>X</button>
    </motion.div>
  )
  
}
