import React from 'react'
import '../styles/knowledge.css';
import {motion } from 'framer-motion';


export const KnowledgeModal = ({data, close}) => {
  return (

    <motion.div className='modal' 
    onClick={(e)=>{
        e.stopPropagation();
    }}
    >   
       <div className='knowledge_modal'>
        <h2 className='tittle_card'>{data.tittle}</h2>
        <img className='icon_inside' src={data.image}></img>
        <button className='modal_btn' onClick={close}>X</button>
       
        <button className='modal_btn' onClick={close}>X</button>
        </div> 
    </motion.div>
  )
  
}
