import React from 'react';
import '../styles/knowledge.css'
import { motion } from 'framer-motion';

const knowledges = [
    {id:0, tittle: "HTML", image: require('../image/icon.png')},
    {id:1, tittle: "CSS", image: require('../image/icon.png')},
    {id:2, tittle: "Javascript", image: require('../image/icon.png')},
    {id:3, tittle: "Reacj.js", image: require('../image/icon.png')},
    {id:4, tittle: "Node.js", image: require('../image/icon.png')},
    {id:5, tittle: "Typescript", image: require('../image/icon.png')},
    {id:6, tittle: "Java", image: require('../image/icon.png')},
    {id:7, tittle: "MySQL", image: require('../image/icon.png')},
    {id:8, tittle: "Git", image: require('../image/icon.png')},
    {id:9, tittle: "C", image: require('../image/icon.png')}
]

const item = {
    hidden: {
        opacity: 0, 
        translateX: 75, 
        translateY: -50
    },
    show:{
        opacity:1, 
        translateX: 0, 
        translateY: 0,

    },
    hover:{
        scale:1.1,
        originX: 0
    }
};

export const Knowledge = () => {
  return (
    <div className='container_ppal_project'>
        <motion.h2 className='subtitulo' initial={{y: -150}} animate={{y:-10}} transition={{delay: 0.1, type: 'spring', stiffness: 75}}>Conocimientos</motion.h2>
        <motion.div className='list_knowledge' initial={{opacity: 0}} animate={{opacity: 1}} transition={{delay: 0.5, duration: 0.5}}>
            <ul className='section_knowledge'>
                {knowledges.map((knowledge, i)=> (
                    <motion.div key={knowledge.id} className='card_knowledge' 
                    variants={item}
                    initial="hidden"
                    animate="show" transition={{
                        duration: 1.5, 
                        delay: i * 0.3
                    }}
                    whileHover="hover">
                        <h3 className='tittle_Card'>{knowledge.tittle}</h3>
                        <img className='icon_inside' src={knowledge.image}></img>
                    </motion.div>
                    ))}
            </ul>
        </motion.div>

    </div>
  )
}



