                    translateY: -50}}
                animate={{opacity:1, 
                    translateX: 0, 
                    translateY: 0}} 
                transition={{
                    duration: 0.9, 
                    delay: 0.25,
                }}>                            
             <h3 className='tittle_card'>Java</h3>
             <img className='icon_inside' src={require('../image/icon.png')} alt='icon'></img>
             {/* <button className='button_card'>Ver Certificado</button> */}
             </motion.div>
             <motion.div className='card_knowledge' initial={{
                    opacity: 0, 
                    translateX: 75, 
                    translateY: -50}}
                animate={{opacity:1, 
                    translateX: 0, 
                    translateY: 0}} 
                transition={{
                    duration: 0.9, 
                    delay: 0.25,
                }}>                            
             <h3 className='tittle_card'>MySQL</h3>
             <img className='icon_inside' src={require('../image/icon.png')} alt='icon'></img>
             {/* <button className='button_card'>Ver Certificado</button> */}
             </motion.div>
             <motion.div className='card_knowledge' initial={{
                    opacity: 0, 
                    translateX: 75, 
                    translateY: -50}}
                animate={{opacity:1, 
                    translateX: 0, 
                    translateY: 0}} 
                transition={{
                    duration: 0.9, 
                    delay: 0.25,
                }}>                            
             <h3 className='tittle_card'>Git</h3>
             <img className='icon_inside' src={require('../image/icon.png')} alt='icon'></img>
             {/* <button className='button_card'>Ver Certificado</button> */}
             </motion.div>
             <motion.div className='card_knowledge' initial={{
                    opacity: 0, 
                    translateX: 75, 
                    translateY: -50}}
                animate={{opacity:1, 
                    translateX: 0, 
                    translateY: 0}} 
                transition={{
                    duration: 0.9, 
                    delay: 0.25,
                }}>                            
