import React from 'react';
import {Formik, Form, Field, ErrorMessage} from 'formik';

const ContactForm = () => {
  return (
    <>
    <Formik
    initialValues={{
        name: '',
        tittle: '',
        email: '',
        message: '',
    }}
    validate={(valores)=>{
        let errores = {};
        if(!valores.name){
            errores.name = 'Ingrese un nombre. Mín 3 caract.';
        }else if(!/[a-zA-Z][a-zA-Z ]/.test(valores.email)){
            errores.name = 'Sólo inserte letras y espacios';
        }
        if(!valores.tittle){
            errores.tittle = 'Ingrese el asunto del mensaje';
        }
        if(!valores.email){
            errores.email = 'Ingrese un email válido';
        }else if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(valores.email)){
            errores.email = 'Email inválido';
        }
        if(!valores.message){
            errores.message = 'Ingrese un mensaje.';
        }else if((valores.message).length < 6){
            errores.message = 'Mensaje muy corto. Caracteres mínimos: 6';
        }

        return errores;
    }}
    onSubmit={(valores)=>{
      console.log('Formulario enviado');  
      console.log(valores);
    }}
    >
        {({values, errors, touched, handleBlur})=>(
            <Form className='formulario'>   
            <div className='div_formulario'>
                <label htmlFor='name'>Nombre: </label>
                <Field type="text" className="input_form" id="name" name='name' placeholder="Ingrese su nombre"  value={values.name} onBlur={handleBlur}/>
                {/* {touched.name && errors.name && <div className='error'>{errors.name}</div>} */}
                <ErrorMessage className='error' name='name' component='div'></ErrorMessage>

            </div>
            <div className='div_formulario'>
                <label htmlFor='tittle'>Asunto: </label>
                <Field type="text" className="input_form" id="tittle" name='tittle' placeholder="Ingrese el asunto"  value={values.tittle} onBlur={handleBlur}/>
                {/* {touched.tittle && errors.tittle && <div className='error'>{errors.tittle}</div>} */}
                <ErrorMessage className='error' name='tittle' component='div'></ErrorMessage>

            </div>
            <div className='div_formulario'>
                <label htmlFor='email'>Email: </label>
                <Field type="email" className="input_form" id="email" name='email' placeholder="name@example.com"  value={values.email} onBlur={handleBlur}/>
                {/* {touched.email && errors.email && <div className='error'>{errors.email}</div>} */}
                <ErrorMessage className='error' name='email' component='div'></ErrorMessage>

            </div>
            <div className='div_formulario'>
                <label htmlFor='message'>Mensaje: </label>
                <Field className="textarea_form" as="textarea" id="message" rows="3" placeholder="Ingrese su mensaje" value={values.message} onBlur={handleBlur}/>
                {/* {touched.message && errors.message && <div className='error'>{errors.message}</div>} */}
                <ErrorMessage className='error' name='message' component='div'></ErrorMessage>

            </div>
            <button type="submit" className="btn btn-outline-info btn-lg">Enviar</button>
            </Form>
        )
        }   
    </Formik>    
    </>
  )
}

export default ContactForm
