import React from 'react';
import {Formik, Form, Field} from 'formik';

const ContactForm = () => {
  return (
    <>
    <Formik
    initialValues={{
        name: '',
        tittle: '',
        email: '',
        message: '',
    }}
    validate={(valores)=>{
        let errores = {};
        if(!valores.name){
            errores.name = 'Ingrese un nombre';
            console.log(errores.name)
        }
    }}
    onSubmit={(valores)=>{
      console.log('Formulario enviado');  
      console.log(valores);
    }}
    >
        {({values, errors, handleSubmit, handleBlur, handleChange})=>(
            <form className='formulario' onSubmit={handleSubmit}>   
            {console.log(errors)}
            <div className='div_formulario'>
                <label htmlFor='name'>Nombre: </label>
                <input type="text" className="input_form" id="name" name='name' placeholder="Ingrese su nombre"  value={values.name} onBlur={handleBlur} onChange={handleChange}/>
                {/* {errors.name && <div className='error'>{errors.name}</div>} */}
                {errors.name && <div className='error'>{errors.name}</div>}

            </div>
            <div className='div_formulario'>
                <label htmlFor='tittle'>Asunto: </label>
                <input type="text" className="input_form" id="tittle" name='tittle' placeholder="Ingrese el asunto"  value={values.tittle} onBlur={handleBlur} onChange={handleChange}/>

            </div>
            <div className='div_formulario'>
                <label htmlFor='email'>Email: </label>
                <input type="email" className="input_form" id="email" name='email' placeholder="name@example.com"  value={values.email} onBlur={handleBlur} onChange={handleChange}/>
                {errors.email && <div className='error'>{errors.email}</div>}
            </div>
            <div className='div_formulario'>
                <label htmlFor='message'>Mensaje: </label>
                <textarea className="textarea_form" id="message" rows="3" placeholder="Ingrese su mensaje" value={values.message} onBlur={handleBlur} onChange={handleChange}/>
            </div>
            <button type="submit" className="btn btn-outline-info btn-lg">Enviar</button>
            </form>
        )
            
        
        }   
    </Formik>    
    </>
  )
}

export default ContactForm
