import React, {useState} from 'react';
import { AnimatePresence, AnimateSharedLayout, motion } from 'framer-motion';
import Modal from './Knowledge_modal';


export const Knowledge_cards = () => {

    const [showModal, setShowModal] = useState(false);
    const [isOpen, setIsOpen] = useState(null);
    const handleClose = () => {
        setShowModal(false);
    }

    const knowledges = [
        {id:0, tittle: "HTML", image: require('../image/icon.png')},
        {id:1, tittle: "CSS", image: require('../image/icon.png')},
        {id:2, tittle: "Javascript", image: require('../image/icon.png')},
        {id:3, tittle: "React.js", image: require('../image/icon.png')},
        {id:4, tittle: "Node.js", image: require('../image/icon.png')},
        {id:5, tittle: "Typescript", image: require('../image/icon.png')},
        {id:6, tittle: "Java", image: require('../image/icon.png')},
        {id:7, tittle: "MySQL", image: require('../image/icon.png')},
        {id:8, tittle: "Git", image: require('../image/icon.png')},
        {id:9, tittle: "C", image: require('../image/icon.png')}
    ]
  return (
        <motion.ul className='section_knowledge' layout >
          {knowledges.map((knowledge, i)=> (
                <>
                <motion.div key={knowledge.id} className='card_knowledge' layoutId={knowledge.id}  
                    initial={{
                        opacity: 0, 
                        translateX: 75, 
                        translateY: -50}}
                    animate={{opacity:1, 
                        translateX: 0, 
                        translateY: 0}} 
                    transition={{
                        duration: 0.5, 
                        delay: i * 0.25,
                    }}
                    onClick={()=>{
                        console.log("clickeado")
                        setShowModal(true);
                        }}>
                    <h3 className='tittle_card'>{knowledge.tittle}</h3>
                    <img className='icon_inside' src={knowledge.image}></img>
                </motion.div>
                {showModal && <Modal onClose={handleClose}>Hello</Modal>}
                </>
                
                 ))}
                 
            </motion.ul>
    
  )
}
