import React from 'react';
import {Formik, Form, Field} from 'formik';

const ContactForm = () => {
  return (
    <>
    <Formik
    initialValues={{
        name: '',
        tittle: '',
        email: '',
        message: '',
    }}
    onSubmit={(valores)=>{
      console.log('Formulario enviado')  
    }}
    >
        {()=>(
            <form className='formulario' >   
            <div className='div_formulario'>
                <label htmlFor='name'>Nombre: </label>
                <input type="text" className="input_form" id="name" name='name' placeholder="Ingrese su nombre" />
            </div>
            <div className='div_formulario'>
                <label htmlFor='tittle'>Asunto: </label>
                <input type="text" className="input_form" id="tittle" name='tittle' placeholder="Ingrese el asunto" />

            </div>
            <div className='div_formulario'>
                <label htmlFor='email'>Email: </label>
                <input type="email" className="input_form" id="email" name='email' placeholder="name@example.com" />

            </div>
            <div className='div_formulario'>
                <label htmlFor='message'>Mensaje: </label>
                <textarea className="textarea_form" id="message" rows="3" placeholder="Ingrese su mensaje"></textarea>

            </div>
            <button type="submit" className="btn btn-outline-info btn-lg">Enviar</button>
            </form>
        )
            
        
        }   
    </Formik>    
    </>
  )
}

export default ContactForm
