import React from 'react'
import '../styles/knowledge.css';
import {motion } from 'framer-motion';


export const Knowledge_modal = ({data, close}) => {
  return (
<div className='modal' 
onClick={(e)=>{
    e.stopPropagation();
}}
>
       <div className='knowledge_modal'>
       <motion.div className='card_knowledge'>
                    <h3 className='tittle_card'>{data.tittle}</h3>
                    <img className='icon_inside' src={data.image}></img>
        </motion.div>
        <button className='modal_btn' onClick={close}>X</button>
        </div> 
    </div>
  )
  
}
