import React from 'react';
import {Formik, Form, Field} from 'formik';

const ContactForm = () => {
  return (
    <>
    <Formik
    initialValues={{
        name: '',
        tittle: '',
        email: '',
        message: '',
    }}
    onSubmit={(valores)=>{
        console.log('formulario enviado exitosamente')
    }
    }
    >
        {({values, errors, handleSubmit, handleChange, handleBlur})=>{
            <Form className='formulario' onSubmit={handleSubmit}>   
            <div>
                <label htmlFor='name'>Nombre: </label>
                <input type="text" className="input_form" id="name" name='name' placeholder="Ingrese su nombre" value={values.name}/>
            </div>
            <div>
                <label htmlFor='tittle'>Asunto: </label>
                <input type="text" className="input_form" id="tittle" name='tittle' placeholder="Ingrese el asunto" value={values.tittle}/>

            </div>
            <div>
                <label htmlFor='email'>Email: </label>
                <input type="email" className="input_form" id="email" name='email' placeholder="name@example.com" value={values.email}/>

            </div>
            <div>
                <label htmlFor='message'>Mensaje: </label>
                <textarea className="textarea_form" id="message" rows="3" placeholder="Ingrese su mensaje"></textarea>

            </div>
            <button type="submit" className="btn btn-outline-info btn-lg">Enviar</button>
            </Form>
        }}
        
    </Formik>    
    </>
  )
}

export default ContactForm
