import React, { Component } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"
// import "slick-carousel/slick/slick.theme.css"

function ImageSlider(){
    let settings = {
        dot:true,
        infinite: true,
        speed: 500,
        slidesToshow: 3,
        slidesToScroll: 1,
        cssEase: "linear"
    }
    return(
        <Slider {...settings}>
            <div className="card-wrapper">
                <div className="card">
                    <div className="card-image">
                        <img src={require('../image/formacion/krause.png')}></img>
                    </div>
                    <ul className="social-icons"><li><a href="#"><i className=""></i></a></li></ul>
                </div>
            </div>
        </Slider>
    )
}

export default ImageSlider;
