import React from 'react'
import { ReactDOM } from 'react';
import '../styles/knowledge.css';

const Knowledge_modal = ({children, onClose}) => {
  return (
    <div className='modal'>
        <div className='knowledge_modal'>
            <button className='modal_btn' onClick={onClose}>X</button>
            {children}
        </div>
    </div>

    
  )
}

export default function ModalPortal({children, onClose}){
    return
    ReactDOM.createPortal(<Knowledge_modal onClose={onClose}>
        {children}
    </Knowledge_modal>, document.getElementById('modal_root'))
    
}
